package id.shoumhome.android.activity;

import androidx.annotation.NonNull;
import androidx.appcompat.app.ActionBar;
import androidx.appcompat.app.AppCompatActivity;
import androidx.appcompat.widget.Toolbar;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.app.NotificationChannel;
import android.content.Intent;
import android.os.Bundle;
import android.view.MenuItem;
import android.view.View;
import android.widget.Spinner;

import java.util.ArrayList;

import id.shoumhome.android.adapters.ListKajianAdapter;
import id.shoumhome.android.R;
import id.shoumhome.android.models.Kajian;

public class KajianActivity extends AppCompatActivity {
    ArrayList<Kajian> mKajian = new ArrayList<>();
    private NotificationChannel v;

    Spinner spJmlKolom;

    //buat di ActionBar
    @Override
    public boolean onOptionsItemSelected(@NonNull MenuItem item) {
        switch (item.getItemId()) {
            case android.R.id.home:
                super.onBackPressed();
                break;
            case R.id.Image_Judul:
                break;
        }
        return true;
    }

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_kajian);

        // fungsi Toolbar dan ActionBar
        Toolbar tb = findViewById(R.id.toolbar);
        setSupportActionBar(tb);
        ActionBar actionBar = getSupportActionBar();
        if (actionBar != null) {
            actionBar.setDisplayHomeAsUpEnabled(true);
        }
        setTitle("Kajian Online");

        Kajian kajian = new Kajian();
        kajian.setId("10");
        kajian.setImgResource("data:image/jpeg;base64,");
        kajian.setTitle("Untukmu Yang Sedang Mencari Ketenangan Jiwa");
        kajian.setUstadzName("Ustad Adi Hidayat Lc., Ma");
        kajian.setMosque("VIDEO");
        kajian.setDate("17/10/2019 17.00");

        mKajian.add(kajian);

        RecyclerView recyclerView = findViewById(R.id.recDataKajian);
        ListKajianAdapter adapter = new ListKajianAdapter(this);
        adapter.setKajian(mKajian);
        recyclerView.setAdapter(adapter);
        recyclerView.setLayoutManager(new LinearLayoutManager(this));

    }
}
